#include <algorithm>
#include <chrono>
#include <iostream>
#include <numeric>
#include <string>
#include <unordered_map>
#include <vector>

#define DEBUG() (true)

#define MEASURE_MEM() (true)
#if MEASURE_MEM()
    #if defined(WIN32) || defined(_WIN32) || defined(__WIN32__) || defined(__NT__)
        #include "Windows.h"
        #include "Psapi.h"
        static std::pair<SIZE_T, SIZE_T> get_mem_usage()
        {
            PROCESS_MEMORY_COUNTERS_EX pmc;
            GetProcessMemoryInfo(GetCurrentProcess(), reinterpret_cast<PROCESS_MEMORY_COUNTERS*>(&pmc), sizeof(pmc));
            SIZE_T virtualMemUsedByMe = pmc.PrivateUsage;
            SIZE_T physMemUsedByMe = pmc.WorkingSetSize;
            return std::make_pair(virtualMemUsedByMe, physMemUsedByMe);
        }
    #else
        #error no implementation for this platform
    #endif  // platform tests
#endif  // MEASURE_MEM()

using namespace std;

class Solution {
public:
    size_t new_str_len(size_t len, size_t front_index, size_t back_index)
    {
        const auto prefix_len = front_index + 1;
        const auto suffix_len = len - back_index;
        const auto similar_ends_len = prefix_len + suffix_len;
        const auto new_str_len = len - similar_ends_len;

#if DEBUG()
        std::cout << "prefix:" << prefix_len << " suffix:" << suffix_len <<
            " similar_ends:" << similar_ends_len << " new_str:" << new_str_len << std::endl;
#endif  // DEBUG()

        return new_str_len;
    }

    std::pair<string, bool> delete_similar_ends(const string& s)
    {
        const auto len = s.length();

#if DEBUG()
        std::cout << "START: " << s << " len:" << len << std::endl;
#endif  // DEBUG()

        size_t front_index = 0;
        size_t back_index = len - 1;
        auto extremal_char = 'X'; // only a, b and c are valid
        auto u = extremal_char;
        auto next_loop = true;
        auto found_any = false;

#if DEBUG()
        std::cout << "front:" << front_index << " back:" << back_index <<
            " char:"<< u << " next_loop:" << next_loop << std::endl;
#endif  // DEBUG()

        for (size_t i = 0, j = (len - 1); (i < len) && (j < len) && next_loop; )
        {
            if (((u == extremal_char) || (u == s[i])) && (i < j) && (s[i] == s[back_index])) {
                front_index = i;
                u = s[i];
                ++i;
                found_any = true;
            }
            else if ((u == s[j]) && (i < j) && (s[front_index] == s[j])) {
                back_index = j;
                --j;
                found_any = true;
            } else {
                next_loop = false;
            }

#if DEBUG()
            std::cout << "front:" << front_index << " back:" << back_index <<
                " char:"<< u << " i:" << i << " j:" << j <<
                " next_loop:" << next_loop << std::endl;
#endif  // DEBUG()
        }

        const auto new_str = found_any
            ? s.substr(front_index + 1, new_str_len(len, front_index, back_index))
            : s;

#if DEBUG()
        std::cout << "found_any:" << found_any << std::endl;
#endif  // DEBUG()

        return std::make_pair(new_str, found_any);
    }

    int minimumLength(string s) {
        auto min_len = 0;

        auto min_str = s;
        auto cont = true;
        while (cont) {
            std::tie(min_str, cont) = delete_similar_ends(min_str);
#if DEBUG()
            std::cout << min_str << ' ' << cont << std::endl;
#endif  // DEBUG()
        }
        min_len = static_cast<int>(min_str.length());  // int is a bad type for length...

        return min_len;
    }
};

int main()
{
    /*  Input: s = "aabccabba"
        Output: 3
        Explanation: An optimal sequence of operations is:
        - Take prefix = "aa" and suffix = "a" and remove them, s = "bccabb".
        - Take prefix = "b" and suffix = "bb" and remove them, s = "cca".

        took 24881900 ns
        virtualMemUsedByMe:1495040 B physMemUsedByMe:4755456 B
    */
    // string i{"aabccabba"};
    string i{""};
    const auto t1 = std::chrono::high_resolution_clock::now();
    const auto res = Solution().minimumLength(i);
    const auto t2 = std::chrono::high_resolution_clock::now();

#if MEASURE_MEM()
    SIZE_T virtualMemUsedByMe, physMemUsedByMe;
    std::tie(virtualMemUsedByMe, physMemUsedByMe) = get_mem_usage();
#endif  // MEASURE_MEM()

    std::cout << res << std::endl;

    const auto duration = std::chrono::duration_cast<std::chrono::nanoseconds>( t2 - t1 ).count();
    std::cout << "took " << duration << " ns" << std::endl;

#if MEASURE_MEM()
    // TODO the unit is probably byte, but I haven't checked. It should matter for comparisons.
    std::cout << "virtualMemUsedByMe:" << virtualMemUsedByMe << " B physMemUsedByMe:" << physMemUsedByMe << " B"<< std::endl;
#endif  // MEASURE_MEM()

    return 0;
}
